mapper.select:
SELECT 

FROM biz_cate_prop_value a

WHERE a.id = #{id}

SELECT 

FROM biz_cate_prop_value a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.prop_id = #{catePropertyInfo.id}





		ORDER BY ${page.orderBy}

SELECT 

FROM biz_cate_prop_value a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

SELECT
FROM biz_cate_prop_value a left Join biz_cate_property_info cpi on cpi.id=a.prop_id Left join biz_prod_cate pc on pc.cat_id = cpi.cat_id left Join biz_product_info pi on pi.id=pc.prod_id Left Join biz_category_info ci on ci.id=pc.cat_id
a.status = 1
and pc.prod_id=#{productInfo.id}
and pc.cat_id=#{categoryInfo.id}
and pc.cat_id in
#{catId}
GROUP BY a.source, a.sys_prop_value_id

mapper.update:
UPDATE biz_cate_prop_value SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
