mapper.select:
SELECT 

FROM biz_cate_property_info a

WHERE a.id = #{id}

SELECT , cpv.id AS "catePropValueList.id", cpv.sys_prop_id AS "propertyInfoId", cpv.sys_prop_value_id AS "propertyValueId" FROM biz_cate_property_info a
LEFT JOIN biz_cate_prop_value cpv ON cpv.prop_id=a.id
AND a.cat_id = #{categoryInfo.id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM biz_cate_property_info a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_cate_property_info SET
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
