mapper.select:
SELECT 

FROM biz_category_info a

WHERE a.id = #{id}

SELECT 

FROM biz_category_info a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.catelog_id = #{catelogInfo.id}


		AND a.p_id = #{parent.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')





		ORDER BY ${page.orderBy}

SELECT 

FROM biz_category_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_category_info SET
p_id = #{parent.id},
p_ids = #{parentIds}
WHERE id = #{id}
