mapper.select:
SELECT 

FROM biz_category_info a

WHERE a.id = #{id}

SELECT 

FROM biz_category_info a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.catelog_id = #{catelogId}


		AND a.p_id = #{pId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_category_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_category_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
