mapper.select:
SELECT

FROM biz_custom_center_consultant a

WHERE a.cust_id = #{customs.id} limit 1;

SELECT

FROM biz_custom_center_consultant a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.cust_id = #{customs.id}


		AND suu.mobile like
		'%'||#{consultants.mobile}||'%'
		'%'+#{consultants.mobile}+'%'
		concat('%',#{consultants.mobile},'%')


		AND a.center_id = #{centers.id}


		AND a.consultant_id = #{consultants.id}




		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_custom_center_consultant SET
status = #{DEL_FLAG_DELETE}
WHERE cust_id = #{customs.id}

mapper.select:
SELECT a.cust_id AS "customs.id", soc. NAME AS "customs.name", soa.addr_id, cl.province_id as "bizLocation.province.id", province.name as "bizLocation.province.name", cl.city_id as "bizLocation.city.id", city.name as "bizLocation.city.name", cl.region_id as "bizLocation.region.id", reg.name as "bizLocation.region.name", cl.address as "bizLocation.address", soc.primary_person as "customs.primaryPerson", pp.name AS "customs.primaryPerson.name", soc.parent_ids AS "customs.parentIds", a.center_id AS "centers.id", sox. NAME AS "centers.name", a.consultant_id AS "consultants.id", sug. NAME AS "consultants.name", a.create_time AS "createDate", a.create_id AS "createBy.id", suc. NAME AS "createBy.name", a.update_time AS "updateDate", a.update_id AS "updateBy.id", a. STATUS AS "delFlag", suu.mobile AS "consultants.mobile" FROM biz_custom_center_consultant a LEFT JOIN sys_office soc ON soc.id = a.cust_id LEFT JOIN sys_office sox ON sox.id = a.center_id LEFT JOIN sys_user suc ON suc.id = a.create_id LEFT JOIN sys_user sug ON sug.id = a.consultant_id LEFT JOIN sys_user suu ON suu.company_id = soc.id AND suu. STATUS = 1
LEFT JOIN sys_user pp ON pp.id = soc.primary_person and pp.status=1 LEFT JOIN sys_office_address soa on soa.office_id=soc.id and soa.status=1 and soa.default_status=1 left join common_location cl on cl.id=soa.addr_id LEFT JOIN sys_region province ON cl.province_id = province.id LEFT JOIN sys_region city ON cl.city_id = city.id LEFT JOIN sys_region reg ON cl.region_id = reg.id
a.status = #{DEL_FLAG_NORMAL}
AND a.cust_id = #{customs.id}
AND suu.mobile like '%'||#{consultants.mobile}||'%' '%'+#{consultants.mobile}+'%' concat('%',#{consultants.mobile},'%')
AND a.create_time <= #{orderHeaderEedTime}
AND a.create_time BETWEEN #{ordrHeaderStartTime} and #{orderHeaderEedTime}
AND a.center_id = #{centers.id}
AND a.consultant_id = #{consultants.id}
ORDER BY ${page.orderBy}

SELECT a.cust_id AS "customs.id", soc.name as"customs.name", soc.parent_ids AS "customs.parentIds", a.center_id AS "centers.id", sox.name as "centers.name", a.consultant_id AS "consultants.id", sug.name as "consultants.name", a.create_time AS "createDate", a.create_id AS "createBy.id", suc.name as "createBy.name", a.update_time AS "updateDate", a.update_id AS "updateBy.id", a.status as "delFlag", suu.mobile as "consultants.mobile", boh.order_num, COUNT(1) AS orderCount FROM biz_custom_center_consultant a LEFT JOIN sys_office soc on soc.id=a.cust_id LEFT JOIN sys_office sox on sox.id=a.center_id LEFT JOIN sys_user suc on suc.id=a.create_id LEFT JOIN sys_user sug on sug.id=a.consultant_id and sug.status=1 LEFT JOIN sys_user suu on suu.company_id=soc.id and suu.status=1 left join biz_order_header boh on boh.cust_id = a.cust_id and boh.status=1
a.status = 1 and boh.order_type = 1 AND NOT EXISTS ( SELECT 1 FROM sys_test_account ta WHERE boh.cust_id = ta.object_id AND ta.object_name = 'sys_office' ) AND ((biz_status != 45 AND biz_status != 35 AND biz_status != 0 AND biz_status != 40) OR (biz_status = 40 AND receive_total > 0)) AND boh.create_time BETWEEN #{startDate} AND #{endDate}
AND a.consultant_id = #{consultantId}
group by a.cust_id

select t.*, COUNT(1) AS orderCount from ( SELECT sox.name AS "centers.name", a.consultant_id AS "consultants.id", sug.name AS "consultants.name" FROM biz_custom_center_consultant a LEFT JOIN sys_office soc on soc.id=a.cust_id LEFT JOIN sys_office sox on sox.id=a.center_id LEFT JOIN sys_user suc on suc.id=a.create_id LEFT JOIN sys_user sug on sug.id=a.consultant_id and sug.status=1 LEFT JOIN sys_user suu on suu.company_id=soc.id and suu.status=1 left join biz_order_header boh on boh.cust_id = a.cust_id and boh.status=1
a.status = 1 and boh.order_type = 1 AND NOT EXISTS ( SELECT 1 FROM sys_test_account ta WHERE boh.cust_id = ta.object_id AND ta.object_name = 'sys_office' ) AND ((biz_status != 45 AND biz_status != 35 AND biz_status != 0 AND biz_status != 40) OR (biz_status = 40 AND receive_total > 0)) AND boh.create_time BETWEEN #{startDate} AND #{endDate}
AND a.center_id = #{purchasingId}
group by a.cust_id
) AS t group by t.`consultants.id`
