mapper.select:
SELECT 

FROM biz_integration_activity a

WHERE a.id = #{id}

SELECT 

FROM biz_integration_activity a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.activity_name = #{activityName}


		AND a.send_time BETWEEN #{beginSendTime} AND #{endSendTime}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_integration_activity a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_integration_activity SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
