mapper.select:
SELECT 

FROM biz_collect_goods_record a

WHERE a.id = #{id}

SELECT
FROM biz_collect_goods_record a
a.status = #{DEL_FLAG_NORMAL}
AND a.inv_id = #{invInfo.id}
AND inv.name LIKE '%'||#{invInfo.name}||'%' '%'+#{invInfo.name}+'%' concat('%',#{invInfo.name},'%')
AND a.sku_id = #{skuInfo.id}
AND sku.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{skuInfo.name},'%')
AND sku.part_no LIKE '%'||#{skuInfo.partNo}||'%' '%'+#{skuInfo.partNo}+'%' concat('%',#{skuInfo.partNo},'%')
AND a.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
${sqlMap.collectGoodsRecord}
ORDER BY ${page.orderBy}
order by updateDate desc

SELECT 

FROM biz_collect_goods_record a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}




ORDER BY createDate DESC

mapper.update:
UPDATE biz_collect_goods_record SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
