mapper.select:
SELECT 

FROM biz_collect_goods_record a

WHERE a.id = #{id}

SELECT 

FROM biz_collect_goods_record a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.inv_id = #{invId}


		AND a.sku_id = #{skuId}


		AND a.order_id = #{orderId}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_collect_goods_record a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_collect_goods_record SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
