mapper.select:
SELECT 

FROM biz_inventory_sku a

WHERE a.id = #{id}

SELECT 

FROM biz_inventory_sku a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.inv_id = #{invInfo.id}


		AND a.inv_id = #{skuInfo.id}


		AND sku.name LIKE
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{skuInfo.name},'%')


		AND a.inv_type != #{invType}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_inventory_sku a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_inventory_sku SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
