mapper.select:
SELECT 

FROM biz_inventory_sku a

WHERE a.id = #{id}

SELECT , (SELECT sum(bsgr.send_num) FROM biz_send_goods_record bsgr LEFT JOIN biz_inventory_sku bis ON bis.inv_id = bsgr.inv_id AND bis.sku_id = bsgr.sku_id WHERE bsgr.biz_status = 0 AND bis.id = a.id) AS "outWarehouse", (SELECT sum(bsgr.send_num) FROM biz_send_goods_record bsgr LEFT JOIN biz_inventory_sku bis ON bis.sku_id = bsgr.sku_id WHERE bsgr.biz_status = 1 AND bsgr.order_id = 0 AND bis.id = a.id) AS "sendGoodsNum", (SELECT sum(bcgr.receive_num) FROM biz_collect_goods_record bcgr LEFT JOIN biz_inventory_sku bis ON bis.inv_id = bcgr.inv_id AND bis.sku_id = bcgr.sku_id WHERE bis.id = a.id) AS "inWarehouse" FROM biz_inventory_sku a
1=1
and a.status = #{DEL_FLAG_NORMAL}
AND a.inv_id = #{invInfo.id}
AND i.cust_id = #{invInfo.customer.id}
AND a.sku_id = #{skuInfo.id}
AND sku.name LIKE '%'||#{skuInfo.name}||'%' '%'+#{skuInfo.name}+'%' concat('%',#{skuInfo.name},'%')
AND bpi.vendor_name LIKE '%'||#{skuInfo.vendorName}||'%' '%'+#{skuInfo.vendorName}+'%' concat('%',#{skuInfo.vendorName},'%')
AND bpi.vari_id = #{skuInfo.variety.id}
AND sku.item_no LIKE '%'||#{skuInfo.itemNo}||'%' '%'+#{skuInfo.itemNo}+'%' concat('%',#{skuInfo.itemNo},'%')
AND sku.part_no LIKE '%'||#{skuInfo.partNo}||'%' '%'+#{skuInfo.partNo}+'%' concat('%',#{skuInfo.partNo},'%')
AND i.name LIKE '%'||#{invInfo.name}||'%' '%'+#{invInfo.name}+'%' concat('%',#{invInfo.name},'%')
AND a.inv_type = #{invType}
${sqlMap.inventorySku}
AND EXISTS ( select r.sku_id from biz_collect_goods_record r, biz_inventory_sku sku where r.inv_id = sku.inv_id and r.sku_id = sku.sku_id and sku.stock_qty > 0 and #{inventoryAgeDate} >= r.receive_time and a.sku_id = r.sku_id group by r.sku_id )
ORDER BY ${page.orderBy}
order by updateDate desc

SELECT 

FROM biz_inventory_sku a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

select

from biz_inventory_sku a

WHERE a.sku_id = #{skuInfo.id}
AND a.inv_id = #{invInfo.id}
AND a.inv_type = #{invType}

mapper.update:
UPDATE biz_inventory_sku SET 
	status = #{delFlag}
WHERE id = #{id}

UPDATE biz_inventory_sku SET

	s_ord_qty = #{stockOrdQty}

WHERE id = #{id}
