mapper.select:
SELECT 

FROM biz_invoice a

WHERE a.id = #{id}

SELECT 

FROM biz_invoice a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.send_number = #{sendNumber}


		AND a.logistics_id = #{logistics}


		AND a.carrier LIKE 
			'%'||#{carrier}||'%'
			'%'+#{carrier}+'%'
			concat('%',#{carrier},'%')


		AND a.settlement_status = #{settlementStatus}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_invoice a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_invoice SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
