mapper.select:
SELECT 

FROM biz_invoice a

WHERE a.id = #{id}

		SELECT 

		FROM biz_invoice a


			a.status = #{DEL_FLAG_NORMAL}

				AND a.send_number LIKE
				'%'||#{sendNumber}||'%'
				'%'+#{sendNumber}+'%'
				concat('%',#{sendNumber},'%')


				AND a.logistics_id = #{logistics.id}


				AND a.biz_status = #{bizStatus}


				AND a.ship = #{ship}


				AND bl.name LIKE
				'%'||#{logistics.name}||'%'
				'%'+#{logistics.name}+'%'
				concat('%',#{logistics.name},'%')


				AND a.carrier LIKE 
					'%'||#{carrier}||'%'
					'%'+#{carrier}+'%'
					concat('%',#{carrier},'%')


				AND a.settlement_status = #{settlementStatus}


                ${sqlMap.bizInvoice}




				ORDER BY ${page.orderBy}

SELECT 

FROM biz_invoice a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_invoice SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
