mapper.select:
SELECT 

FROM biz_logistics a

WHERE a.id = #{id}

SELECT
FROM biz_logistics a
a.status = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM biz_logistics a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_logistics SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
