mapper.select:
SELECT

FROM biz_send_goods_record a

WHERE a.id = #{id}

SELECT
FROM biz_send_goods_record a
a.status = #{DEL_FLAG_NORMAL}
AND sku.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{skuInfo.name},'%')
AND orheader.order_num LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{orderHeader.orderNum},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM biz_send_goods_record a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_send_goods_record SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
