mapper.select:
SELECT
FROM biz_send_goods_record a
1=1
and a.status = #{DEL_FLAG_NORMAL}
AND a.send_no LIKE #{sendNo} '%'||#{sendNo}||'%' '%'+#{sendNo}+'%' concat('%',#{sendNo},'%')
AND a.biz_status = #{bizStatus}
AND a.send_number LIKE '%'||#{carrier}||'%' '%'+#{carrier}+'%' concat('%',#{carrier},'%')
AND a.sku_id = #{skuInfo.id}
AND a.inv_id = #{invInfo.id}
AND sku.name LIKE '%'||#{skuInfo.name}||'%' '%'+#{skuInfo.name}+'%' concat('%',#{skuInfo.name},'%')
AND sku.part_no LIKE '%'||#{skuInfo.partNo}||'%' '%'+#{skuInfo.partNo}+'%' concat('%',#{skuInfo.partNo},'%')
AND sku.item_no LIKE '%'||#{skuInfo.itemNo}||'%' '%'+#{skuInfo.itemNo}+'%' concat('%',#{skuInfo.itemNo},'%')
AND inv.name LIKE '%'||#{invInfo.name}||'%' '%'+#{invInfo.name}+'%' concat('%',#{invInfo.name},'%')
AND a.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
${sqlMap.sendGoodsRecord}
ORDER BY ${page.orderBy}
ORDER BY updateDate DESC

SELECT 

FROM biz_send_goods_record a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_send_goods_record SET 
	status = #{delFlag}
WHERE id = #{id}

UPDATE biz_send_goods_record SET
sku_id = #{needSkuId}
WHERE id = #{id}
