mapper.select:
SELECT 

FROM biz_send_goods_record a

WHERE a.id = #{id}

SELECT 

FROM biz_send_goods_record a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.sku_id = #{skuInfo}


		AND a.order_id = #{orderHeader}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_send_goods_record a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_send_goods_record SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
