mapper.select:
SELECT 

FROM biz_inventory_view_log a

WHERE a.id = #{id}

SELECT
FROM biz_inventory_view_log a
a.status = #{DEL_FLAG_NORMAL}
AND bi.name LIKE '%'||#{invInfo.name}||'%' '%'+#{invInfo.name}+'%' concat('%',#{invInfo.name},'%')
AND bsi.name LIKE '%'||#{skuInfo.name}||'%' '%'+#{skuInfo.name}+'%' concat('%',#{skuInfo.name},'%')
${sqlMap.viewLog}
ORDER BY ${page.orderBy}
ORDER BY a.create_time DESC

SELECT 

FROM biz_inventory_view_log a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_inventory_view_log SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
