mapper.select:
SELECT
FROM biz_commission a
LEFT JOIN biz_order_header boh ON boh.id=bco.order_id AND boh.status=1
a.status = #{DEL_FLAG_NORMAL}
AND so.name like '%'||#{customerName}||'%' '%'+#{customerName}+'%' concat('%',#{customerName},'%')
AND a.biz_status = #{bizStatus}
AND boh.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
AND NOT EXISTS ( SELECT 1 FROM sys_test_account ta WHERE a.seller_id = ta.object_id AND ta.object_name = 'sys_office' )
ORDER BY ${page.orderBy}
GROUP BY a.id

SELECT
FROM biz_commission a LEFT JOIN common_process process ON process.object_id = a.id and process.object_name = 'biz_commission' and process.current = 1 LEFT JOIN sys_office so ON so.id = a.seller_id LEFT JOIN biz_customer_info bci ON bci.office_id = so.id LEFT JOIN biz_commission_order bco ON bco.comm_id = a.id
LEFT JOIN biz_order_header boh ON boh.id=bco.order_id AND boh.status=1
a.status = #{DEL_FLAG_NORMAL}
AND so.name like '%'||#{customerName}||'%' '%'+#{customerName}+'%' concat('%',#{customerName},'%')
AND a.biz_status = #{bizStatus}
AND boh.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
AND NOT EXISTS ( SELECT 1 FROM sys_test_account ta WHERE a.seller_id = ta.object_id AND ta.object_name = 'sys_office' )
ORDER BY ${page.orderBy}
GROUP BY a.id

SELECT 

FROM biz_commission a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		GROUP  BY a.id

mapper.update:
UPDATE biz_commission SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
