mapper.select:
SELECT 

FROM biz_commission a

WHERE a.id = #{id}

SELECT
FROM biz_commission a
LEFT JOIN biz_commission_order bco ON bco.comm_id=a.id LEFT JOIN biz_order_header boh ON boh.id=bco.order_id AND boh.status=1
a.status = #{DEL_FLAG_NORMAL}
AND bci.payee like '%'||#{payee}||'%' '%'+#{payee}+'%' concat('%',#{payee},'%')
AND a.biz_status = #{bizStatus}
AND boh.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM biz_commission a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_commission SET total_commission = #{totalCommission}, pay_total = #{payTotal}, img_url = #{imgUrl}, deadline = #{deadline}, pay_time = #{payTime}, remark = #{remark}, biz_status = #{bizStatus},
seller_id = #{sellerId},
u_version = #{uVersion}, update_id = #{updateBy.id}, update_time = #{updateDate} WHERE id = #{id}

UPDATE biz_commission SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
