mapper.select:
SELECT 

FROM biz_commission_order a

WHERE a.id = #{id}

SELECT 

FROM biz_commission_order a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.comm_id = #{commId}


		AND a.order_id = #{orderId}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_commission_order a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_commission_order SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
