SELECT 

FROM biz_freight_config a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.office_id = #{office.id}


		AND a.var_id = #{varietyInfo.id}


GROUP BY a.office_id,a.var_id


		ORDER BY ${page.orderBy}


		ORDER BY a.update_time

