mapper.select:
SELECT 

FROM biz_order_detail a

WHERE a.id = #{id}

SELECT 

FROM biz_order_detail a


	  a.status = #{DEL_FLAG_NORMAL}

		AND a.order_id = #{orderHeader.id}


		AND boh.biz_status BETWEEN #{orderHeader.bizStatusStart} and #{orderHeader.bizStatusEnd}


		AND a.line_no = #{lineNo}


		AND a.p_line_no = #{pLineNo}


		AND a.sku_shelf_id = #{shelfInfo.id}


		AND a.sku_id = #{skuInfo.id}


		AND a.suply_id = #{suplyis.id}


		AND a.create_id = #{createBy.id}


		AND a.u_version = #{uVersion}


		AND a.update_id = #{updateBy.id}




		ORDER BY ${page.orderBy}




order by a.line_no ASC

SELECT GROUP_CONCAT( od.id ) AS "detailIds", GROUP_CONCAT( od.suply_id ) AS "suplyIds", sum( od.ord_qty ) AS "totalReqQty", sum( od.sent_qty ) AS "totalSendQty", pi.vendor_id AS "vendorId", pi.vendor_name AS "vendorName" FROM ( SELECT a.id , a.sku_id,a.ord_qty,a.sent_qty,a.order_id,a.suply_id,a.line_no FROM biz_order_detail a WHERE a.suply_id =0 and a.ord_qty > a.sent_qty and NOT EXISTS ( SELECT por.so_line_no, por.so_id FROM biz_po_order_req por WHERE por.status = 1 AND por.so_line_no = a.line_no AND por.so_id = a.order_id AND por.so_type = 1 ) ) od LEFT JOIN biz_order_header oh ON od.order_id = oh.id JOIN biz_sku_info si ON si.id = od.sku_id JOIN biz_product_info pi ON pi.id = si.prod_id WHERE oh.STATUS = 1
AND oh.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}
AND oh.biz_status != #{bizStatusNot}
GROUP BY pi.vendor_id

SELECT GROUP_CONCAT( reqDetailIds ) AS reqDetailIds, GROUP_CONCAT( detailIds ) AS detailIds, sum( totalReqQty ) AS "totalReqQty", sum( totalOrdQty ) AS "totalOrdQty", vendorId AS "vendorId", vendorName AS "vendorName" FROM ( ( SELECT GROUP_CONCAT( rd.id ) AS "reqDetailIds", '' AS "detailIds", sum( rd.req_qty ) AS "totalReqQty", 0 AS "totalOrdQty", rd.vendor_id AS "vendorId", rd.vendor_name AS "vendorName" FROM ( SELECT rd.id, rd.sku_id, rd.req_qty, rd.send_qty, rd.recv_qty, rd.header_id, rd.line_no, pi.vendor_id, pi.vendor_name FROM biz_request_header rh, biz_request_detail rd, biz_sku_info si, biz_product_info pi WHERE rh.biz_status BETWEEN 5 AND 20 AND rh.biz_status != 15 AND rh.STATUS = 1 AND rd.STATUS = 1 AND rd.header_id = rh.id AND si.id = rd.sku_id AND pi.id = si.prod_id AND NOT EXISTS ( SELECT por.so_line_no, por.so_id FROM biz_po_order_req por ,biz_po_header ph WHERE por.status = 1 AND por.so_line_no = rd.line_no AND por.so_id = rd.header_id AND por.so_type = 2 and ph.id=por.po_id and is_prew=0 and ph.status=1 ) AND NOT EXISTS ( SELECT 1 FROM sys_test_account ta WHERE rh.from_id = ta.object_id AND ta.object_name = 'sys_office' ) ) rd GROUP BY rd.vendor_id ) UNION ALL ( SELECT '' AS "reqDetailIds", GROUP_CONCAT( od.id ) AS "detailIds", 0 AS "totalReqQty", sum( od.ord_qty ) AS "totalOrdQty", od.vendor_id AS "vendorId", od.vendor_name AS "vendorName" FROM ( SELECT od.id, od.sku_id, od.ord_qty, od.sent_qty, od.order_id, od.suply_id, od.line_no, pi.vendor_id, pi.vendor_name FROM biz_order_header oh, biz_order_detail od, biz_sku_info si, biz_product_info pi WHERE ( od.suply_id = 0 OR od.suply_id = 721 ) AND od.order_id = oh.id AND oh.STATUS = 1 AND od.STATUS = 1 AND oh.order_type != 5 AND oh.biz_status BETWEEN 15 AND 19 AND oh.biz_status != 18 AND od.ord_qty > od.sent_qty AND si.id = od.sku_id AND pi.id = si.prod_id AND NOT EXISTS ( SELECT por.so_line_no, por.so_id FROM biz_po_order_req por ,biz_po_header ph WHERE por.status = 1 AND por.so_line_no = od.line_no AND por.so_id = od.order_id AND por.so_type = 1 and ph.id=por.po_id and is_prew=0 and ph.status=1 ) AND NOT EXISTS ( SELECT 1 FROM sys_test_account ta WHERE oh.cust_id = ta.object_id AND ta.object_name = 'sys_office' ) ) od GROUP BY od.vendor_id ) ) t1 WHERE 1 =1
and t1.vendorName = #{name}
GROUP BY vendorId

SELECT 

FROM biz_order_detail a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_order_detail SET
status=#{DEL_FLAG_DELETE}
