mapper.select:
SELECT a.id AS "id", a.order_id AS "orderHeader.id", boh.order_num AS "orderHeader.orderNum", a.line_no AS "lineNo", a.p_line_no AS "pLineNo", a.sku_shelf_id as "shelfInfo.id", si.name as "shelfInfo.opShelfInfo.name", a.sku_id AS "skuInfo.id", bsi.name as "skuInfo.name", a.buy_price as "buyPrice",
a.part_no AS "partNo", a.sku_name AS "skuName", bsi.item_no AS "skuInfo.itemNo", a.unit_price AS "unitPrice", a.sent_qty as "sentQty", a.ord_qty AS "ordQty", a.suply_id as "suplyis.id", soo.name as "suplyis.name", a.create_id AS "createBy.id", su.name as "createBy.name", a.create_time AS "createDate", a.u_version AS "uVersion", a.update_id AS "updateBy.id", a.update_time AS "updateDate", off.id AS "vendor.id", off.name AS "vendor.name", us.id AS "primary.id", us.mobile AS "primary.mobile", ph.id AS "poHeader.id", ph.order_num AS "poHeader.orderNum", ph.remark AS "poHeader.remark" FROM biz_order_detail a LEFT join sys_user su on a.create_id=su.id left join biz_sku_info bsi on a.sku_id=bsi.id left join biz_order_header boh on a.order_id=boh.id LEFT JOIN biz_op_shelf_sku ois on a.sku_shelf_id=ois.id Left Join biz_op_shelf_info si on si.id = ois.shelf_id
LEFT join sys_office soo on soo.id=a.suply_id LEFT JOIN biz_product_info pro ON pro.id = bsi.prod_id LEFT JOIN sys_office off ON off.id = pro.vendor_id LEFT JOIN sys_user us ON us.id = off.primary_person
LEFT JOIN biz_po_order_req bpor ON bpor.so_id = a.order_id AND bpor.status = 1 LEFT JOIN biz_po_header ph ON ph.id = bpor.po_id and ph.status=1 and ph.is_prew=0
a.status=1 AND a.line_no = bpor.so_line_no and bpor.so_type=1
AND a.order_id = #{orderHeader.id}
AND boh.biz_status BETWEEN #{orderHeader.bizStatusStart} and #{orderHeader.bizStatusEnd}
AND a.line_no = #{lineNo}
AND a.p_line_no = #{pLineNo}
AND a.sku_shelf_id = #{shelfInfo.id}
AND a.sku_id = #{skuInfo.id}
AND a.suply_id = #{suplyis.id}
AND a.create_id = #{createBy.id}
AND a.u_version = #{uVersion}
AND a.update_id = #{updateBy.id}
ORDER BY ${page.orderBy}
order by a.line_no ASC

