mapper.select:
SELECT 

FROM biz_order_detail a

WHERE a.id = #{id}

SELECT 

FROM biz_order_detail a




		AND a.order_id = #{orderHeader.id}


		AND boh.biz_status BETWEEN #{orderHeader.bizStatusStart} and #{orderHeader.bizStatusEnd}


		AND a.line_no = #{lineNo}


		AND a.p_line_no = #{pLineNo}


		AND a.sku_shelf_id = #{shelfInfo.id}


		AND a.sku_id = #{skuInfo.id}


		AND a.suply_id = #{suplyis.id}


		AND a.create_id = #{createBy.id}


		AND a.u_version = #{uVersion}


		AND a.update_id = #{updateBy.id}




		ORDER BY ${page.orderBy}




order by createDate ASC

SELECT GROUP_CONCAT( od.id ) AS "detailIds", GROUP_CONCAT( od.suply_id ) AS "suplyIds", sum( od.ord_qty ) AS "totalReqQty", sum( od.sent_qty ) AS "totalSendQty", pi.vendor_id AS "vendorId", pi.vendor_name AS "vendorName" FROM ( SELECT a.id , a.sku_id,a.ord_qty,a.sent_qty,a.order_id,a.suply_id,a.line_no FROM biz_order_detail a WHERE a.suply_id =0 and a.ord_qty > a.sent_qty and NOT EXISTS ( SELECT por.so_line_no, por.so_id FROM biz_po_order_req por WHERE por.so_line_no = a.line_no AND por.so_id = a.order_id AND por.so_type = 1 ) ) od LEFT JOIN biz_order_header oh ON od.order_id = oh.id JOIN biz_sku_info si ON si.id = od.sku_id JOIN biz_product_info pi ON pi.id = si.prod_id WHERE oh.STATUS = 1
AND oh.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}
AND oh.biz_status != #{bizStatusNot}
GROUP BY pi.vendor_id

SELECT 

FROM biz_order_detail a






		ORDER BY ${page.orderBy}
