mapper.select:
SELECT

FROM biz_order_header a

WHERE a.id = #{id}

SELECT , od.id AS "orderDetailList.id", od.unit_price AS "orderDetailList.unitPrice", od.ord_qty AS "orderDetailList.ordQty", od.sent_qty AS "orderDetailList.sentQty", od.line_no AS "orderDetailList.lineNo", si.id AS "orderDetailList.skuInfo.id", si.buy_price AS "orderDetailList.skuInfo.buyPrice", si.item_no AS "orderDetailList.skuInfo.itemNo" FROM biz_order_header a LEFT JOIN biz_order_detail od ON od.order_id = a.id LEFT JOIN biz_sku_info si ON si.id=od.sku_id
a.status = #{DEL_FLAG_NORMAL}
AND a.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
AND a.order_type = #{orderType}
AND a.cust_id = #{customer.id}
AND s.name like '%'||#{centersName}||'%' '%'+#{centersName}+'%' concat('%',#{centersName},'%')
AND su.mobile LIKE '%'||#{customer.phone}||'%' '%'+#{customer.phone}+'%' concat('%',#{customer.phone},'%')
AND a.receive_total = #{receiveTotal}
AND a.biz_status=#{bizStatus}
AND a.biz_type=#{bizType}
AND a.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}
AND a.inv_status=#{invStatus}
and od.suply_id !=0
and ccs.consultant_id = #{consultantId}
and ccs.center_id = #{centerId}
and si.item_no LIKE '%'||#{itemNo}||'%' '%'+#{itemNo}+'%' concat('%',#{itemNo},'%')
and si.part_no LIKE '%'||#{partNo}||'%' '%'+#{partNo}+'%' concat('%',#{partNo},'%')
${sqlMap.order}
ORDER BY ${page.orderBy}
-- GROUP BY a.id
ORDER BY updateDate DESC

SELECT count(1) from (SELECT , od.id AS "orderDetailList.id", od.unit_price AS "orderDetailList.unitPrice", od.ord_qty AS "orderDetailList.ordQty", od.sent_qty AS "orderDetailList.sentQty", si.id AS "orderDetailList.skuInfo.id", si.buy_price AS "orderDetailList.skuInfo.buyPrice", si.item_no AS "orderDetailList.skuInfo.itemNo" FROM biz_order_header a LEFT JOIN biz_order_detail od ON od.order_id = a.id LEFT JOIN biz_sku_info si ON si.id=od.sku_id
a.status = #{DEL_FLAG_NORMAL}
AND a.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
AND a.order_type = #{orderType}
AND a.cust_id = #{customer.id}
AND su.mobile LIKE '%'||#{customer.phone}||'%' '%'+#{customer.phone}+'%' concat('%',#{customer.phone},'%')
AND a.receive_total = #{receiveTotal}
AND a.biz_status=#{bizStatus}
AND a.biz_type=#{bizType}
AND a.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}
AND a.inv_status=#{invStatus}
and ccs.consultant_id = #{consultantId}
and ccs.center_id = #{centerId}
and si.item_no LIKE '%'||#{itemNo}||'%' '%'+#{itemNo}+'%' concat('%',#{itemNo},'%')
and si.part_no LIKE '%'||#{partNo}||'%' '%'+#{partNo}+'%' concat('%',#{partNo},'%')
${sqlMap.order}
GROUP BY a.id ) tmp_count

SELECT 

FROM biz_order_header a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}




ORDER BY updateDate DESC

