mapper.select:
SELECT DISTINCT
sum(oh.total_detail + oh.total_exp + oh.freight) AS totalMoney,
od.sku_id AS skuId, pi.name, pi.item_no AS itemNo, pi.vendor_name AS vendorName, ss.sale_price AS salePrice, count(1) as count, (SELECT count(1) FROM biz_prod_view_log AS pvLog WHERE pvLog.prod_id = si.prod_id) AS clickCount
from biz_order_header oh, biz_order_detail od,biz_sku_info si,biz_product_info pi,biz_op_shelf_sku ss, biz_custom_center_consultant ccc
WHERE oh.id = od.order_id
and si.id = od.sku_id
and si.prod_id = pi.id
and pi.vari_id = #{variId}
and ccc.cust_id = oh.cust_id
and oh.`status` = ${@com.wanhutong.backend.common.persistence.BaseEntity@DEL_FLAG_NORMAL}
and si.`status` = ${@com.wanhutong.backend.common.persistence.BaseEntity@DEL_FLAG_NORMAL}
and pi.`status` = ${@com.wanhutong.backend.common.persistence.BaseEntity@DEL_FLAG_NORMAL}
and ss.`status` = ${@com.wanhutong.backend.common.persistence.BaseEntity@DEL_FLAG_NORMAL}
and ss.sku_id = si.id
AND date_format(oh.create_time, '%Y-%m') = #{month}

	AND ccc.center_id = #{purchasingId}

GROUP BY si.prod_id
ORDER BY count DESC
LIMIT 10

SELECT sum(total_detail + total_exp + freight) AS totalMoney, count(1) AS orderCount, us.name AS name, us.id AS usId, date_format(oh.create_time, '%Y-%m') AS createTime FROM biz_order_header oh LEFT JOIN sys_office off ON off.id = oh.cust_id LEFT JOIN biz_custom_center_consultant ccc ON ccc.cust_id = off.id LEFT JOIN sys_user us ON us.id = ccc.consultant_id WHERE oh.`status` = 1 AND (oh.biz_status IN (15,17,18,19,20,25,30) OR ( oh.biz_status = 40 AND oh.receive_total > 0))
AND date_format(oh.create_time, '%Y-%m') = #{month}
AND us.office_id = #{purchasingId}
AND us.id = #{usId}
GROUP BY us.`name`

SELECT sum(total_detail + total_exp + freight) AS totalMoney,
sum( sku.buy_price * detail.ord_qty ) AS buyPrice, ( sum( total_detail + total_exp + freight ) - sum(sku.buy_price) ) AS fright, (SELECT count(1) FROM biz_custom_center_consultant cc WHERE cc.consultant_id = us.id ) AS custCount, (SELECT count(1) FROM biz_custom_center_consultant cc WHERE cc.consultant_id = us.id AND date_format(us.create_date, '%Y-%m') = #{month} ) AS addCustCount,
count(1) AS orderCount, us.name AS name, us.id AS usId, date_format(oh.create_time, '%Y-%m') AS createTime FROM biz_order_header oh LEFT JOIN sys_office off ON off.id = oh.cust_id LEFT JOIN biz_custom_center_consultant ccc ON ccc.cust_id = off.id LEFT JOIN sys_user us ON us.id = ccc.consultant_id
LEFT JOIN biz_order_detail AS detail ON detail.order_id = oh.id LEFT JOIN biz_sku_info AS sku ON sku.id = detail.sku_id
WHERE oh.`status` = 1 AND (oh.biz_status IN (15,17,18,19,20,25,30) OR ( oh.biz_status = 40 AND oh.receive_total > 0))
AND date_format(oh.create_time, '%Y-%m') = #{month}
AND us.office_id = #{purchasingId}
AND us.id = #{usId}
GROUP BY createTime

