mapper.select:
SELECT

FROM biz_order_header a

WHERE a.id = #{id}

		SELECT

		FROM biz_order_header a

			LEFT JOIN biz_order_header_unline ohu on ohu.order_id = a.id


		 1=1 and a.order_type !=4

				and a.status = #{DEL_FLAG_NORMAL}


				AND a.order_num
				LIKE
				'%'||#{orderNum}||'%'
				'%'+#{orderNum}+'%'
				concat('%',#{orderNum},'%')


				AND (
				(
				biz_status != 45
				AND biz_status != 35
				AND biz_status != 0
				)
				OR (biz_status = ${@com.wanhutong.backend.modules.enums.OrderHeaderBizStatusEnum@DELETE.state} AND receive_total > 0)
				)


				AND a.order_type = #{orderType}


				AND a.cust_id = #{customer.id}


				AND s.name like
				'%'||#{centersName}||'%'
				'%'+#{centersName}+'%'
				concat('%',#{centersName},'%')


				AND su.mobile LIKE
				'%'||#{customer.phone}||'%'
				'%'+#{customer.phone}+'%'
				concat('%',#{customer.phone},'%')


				AND a.receive_total = #{receiveTotal}


				AND a.biz_status=#{bizStatus}



				AND a.create_time >= #{orderCreatStartTime}

                    AND a.create_time <= #{orderCreatEndTime}



				AND a.update_time >= #{orderUpdaStartTime}

                    AND a.update_time <= #{orderUpdaEndTime}




				AND a.biz_type=#{bizType}


				AND a.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}


				AND a.inv_status=#{invStatus}


				and ccs.consultant_id = #{consultantId}



				and ccs.center_id = #{centerId}



				and con.name
				LIKE
				'%'||#{con.name}||'%'
				'%'+#{con.name}+'%'
				concat('%',#{con.name},'%')


				AND ohu.id IS NOT NULL


				AND (ohu.biz_status = 0 OR ohu.biz_status = 2)


				AND a.id NOT IN (
					SELECT b.id
					FROM biz_order_header b
					LEFT JOIN biz_order_header_unline bohu ON bohu.order_id = b.id
					WHERE (bohu.biz_status = 0 OR bohu.biz_status = 2)
					AND bohu.`status` = 1
					AND b.`status` = 1
					AND b.order_type != 4
				)


				AND NOT EXISTS(
				select 1
				from sys_test_account ta
				where a.cust_id = ta.object_id
				and ta.object_name = 'sys_office'
				)



				${sqlMap.order}




				ORDER BY ${page.orderBy}


				GROUP  BY a.id


		ORDER BY updateDate DESC

SELECT count(1) from
(SELECT

FROM biz_order_header a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.order_num
		LIKE
		'%'||#{orderNum}||'%'
		'%'+#{orderNum}+'%'
		concat('%',#{orderNum},'%')


		AND s.name like
		'%'||#{centersName}||'%'
		'%'+#{centersName}+'%'
		concat('%',#{centersName},'%')


		AND a.order_type = #{orderType}


		AND a.cust_id = #{customer.id}


		AND su.mobile LIKE
		'%'||#{customer.phone}||'%'
		'%'+#{customer.phone}+'%'
		concat('%',#{customer.phone},'%')


		AND a.receive_total = #{receiveTotal}


		AND a.biz_status=#{bizStatus}


		AND a.biz_type=#{bizType}


		AND a.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}


		AND a.inv_status=#{invStatus}


		and ccs.consultant_id = #{consultantId}



		and ccs.center_id = #{centerId}



		and con.name
		LIKE
		'%'||#{con.name}||'%'
		'%'+#{con.name}+'%'
		concat('%',#{con.name},'%')



		${sqlMap.order}





GROUP  BY a.id
) tmp_count

SELECT

FROM biz_order_header a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}




ORDER BY updateDate DESC

mapper.update:
UPDATE biz_order_header SET 
	status = #{delFlag}
WHERE id = #{id}
