mapper.select:
SELECT 

FROM biz_platform_info a

WHERE a.id = #{id}

SELECT 

FROM biz_platform_info a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.create_id = #{createId.id}


		AND a.update_id = #{updateId.id}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_platform_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_platform_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
