mapper.select:
SELECT 

FROM biz_pay_record a

WHERE a.id = #{id}

SELECT
FROM biz_pay_record a
1=1
and a.status = #{DEL_FLAG_NORMAL}
and a.cust_id=a.account
AND sof.NAME LIKE '%'||#{custConsultant.centers.name}||'%' '%'+#{custConsultant.centers.name}+'%' concat('%',#{custConsultant.centers.name},'%')
AND a.pay_num LIKE '%'||#{payNum}||'%' '%'+#{payNum}+'%' concat('%',#{payNum},'%')
AND a.out_trade_no LIKE '%'||#{outTradeNo}||'%' '%'+#{outTradeNo}+'%' concat('%',#{outTradeNo},'%')
AND a.pay_money = #{payMoney}
AND a.payer = #{payer}
AND so.NAME LIKE '%'||#{customer.name}||'%' '%'+#{customer.name}+'%' concat('%',#{customer.name},'%')
AND sua.mobile LIKE '%'||#{customer.moblieMoeny.mobile}||'%' '%'+#{customer.moblieMoeny.mobile}+'%' concat('%',#{customer.moblieMoeny.mobile},'%')
AND a.create_time BETWEEN #{trandStartTime} and #{trandEndTime}
AND a.create_time >= #{trandStartTime}
AND a.create_time <= #{trandStartTime}
AND a.biz_status = #{bizStatus}
AND o.name like '%'||#{account.name}||'%' '%'+#{account.name}+'%' concat('%',#{account.name},'%')
AND a.to_account = #{toAccount}
AND a.record_type = #{recordType}
AND a.record_type_name = #{recordTypeName}
AND a.pay_type = #{payType}
AND a.pay_type_name = #{payTypeName}
AND a.trade_reason = #{tradeReason}
AND su.name LIKE '%'||#{createBy.name}||'%' '%'+#{createBy.name}+'%' concat('%',#{createBy.name},'%')
ORDER BY ${page.orderBy}
order by a.update_time desc

SELECT 

FROM biz_pay_record a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
