mapper.select:
SELECT

FROM biz_op_plan a

WHERE a.id = #{id}

SELECT

FROM biz_op_plan a


	a.status = #{DEL_FLAG_NORMAL}

		AND s.name like
		'%'||#{objectName1}||'%'
		'%'+#{objectName1}+'%'
		concat('%',#{objectName1},'%')


		AND su.name like
		'%'||#{objectName2}||'%'
		'%'+#{objectName2}+'%'
		concat('%',#{objectName2},'%')


		AND a.year = #{year}


		AND a.month = #{month}


		AND a.day = #{day}


		AND a.amount = #{amount}




		ORDER BY ${page.orderBy}

SELECT

FROM biz_op_plan a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_op_plan SET
status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
