mapper.select:
SELECT 

FROM biz_op_plan a

WHERE a.id = #{id}

SELECT 

FROM biz_op_plan a


	1 = 1

	AND a.status = #{DEL_FLAG_NORMAL}


		AND a.object_id = #{objectId}


		AND a.object_Name = #{objectName}


		AND a.year = #{year}


		AND a.month = #{month}


		AND a.day = #{day}


		AND a.amount = #{amount}


		AND a.u_version = #{uVersion}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_op_plan a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_op_plan SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
