mapper.select:
SELECT 

FROM biz_po_detail a

WHERE a.id = #{id}

SELECT 

	,bsp.object_id AS "schedulingPlan.objectId"
FROM biz_po_detail a
	LEFT JOIN biz_scheduling_plan bsp ON bsp.object_id = a.id



		AND a.order_id = #{poHeader.id}


		AND a.part_no = #{partNo}


		AND a.sku_name = #{skuName}


		AND a.sku_no = #{skuInfo.id}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_po_detail a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_po_detail SET
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
