mapper.select:
SELECT 

FROM biz_po_header a

WHERE a.id = #{id}

SELECT
/*,(SELECT SUM(bpd.ord_qty) FROM biz_po_header bph LEFT JOIN biz_po_detail bpd ON bpd.order_id = bph.id WHERE bph.id = a.id ) AS "totalOrdQty" ,(SELECT SUM(bcp.complete_num) FROM biz_po_header bph LEFT JOIN biz_scheduling_plan bsp ON bsp.object_id = bph.id AND bsp.status=1 AND bsp.object_name = 'biz_po_header' LEFT JOIN biz_complete_paln bcp ON bcp.scheduling_id = bsp.id and bcp.status=1 WHERE bph.status = 1 AND bph.id = a.id) AS "totalSchedulingHeaderNum" ,(SELECT SUM(bcp.complete_num) FROM biz_po_header bph LEFT JOIN biz_po_detail bpd on bpd.order_id = bph.id AND bpd.status=1 LEFT JOIN biz_scheduling_plan bsp ON bsp.object_id = bpd.id AND bsp.status=1 AND bsp.object_name = 'biz_po_detail' LEFT JOIN biz_complete_paln bcp ON bcp.scheduling_id = bsp.id and bcp.status=1 WHERE bph.status = 1 AND bph.id = a.id) AS "totalSchedulingDetailNum",*/ ,(SELECT bsp.po_sch_type FROM biz_po_header bph LEFT JOIN biz_scheduling_plan bsp ON bsp.object_id = bph.id AND bsp.object_name='biz_po_header' AND bsp.status = 1 WHERE bph.id = a.id) AS "poSchType", rh.id AS "bizRequestHeader.id", rh.req_no AS "bizRequestHeader.reqNo", rh.biz_status AS "bizRequestHeader.bizStatus", rh.create_time AS "bizRequestHeader.createDate", oh.id AS "bizOrderHeader.id", oh.order_num AS "bizOrderHeader.orderNum", oh.biz_status AS "bizOrderHeader.bizStatus", oh.create_time AS "bizOrderHeader.createDate" FROM biz_po_header a
LEFT JOIN biz_po_payment_order ppo ON ppo.po_header_id = a.id LEFT JOIN biz_po_order_req por ON por.po_id = a.id LEFT JOIN biz_order_header oh ON oh.id = por.so_id AND por.so_type = 1 LEFT JOIN biz_request_header rh ON rh.id = por.so_id AND por.so_type = 2
a.status = #{DEL_FLAG_NORMAL} AND a.is_prew = 0
AND (oh.order_num LIKE '%'||#{num}||'%' '%'+#{num}+'%' concat('%',#{num},'%') OR rh.req_no LIKE '%'||#{num}||'%' '%'+#{num}+'%' concat('%',#{num},'%') )
AND oh.id = #{bizOrderHeader.id}
AND oh.biz_status = #{bizOrderHeader.bizStatus}
AND rh.id = #{bizRequestHeader.id}
AND rh.biz_status = #{bizRequestHeader.bizStatus}
AND a.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
AND a.vend_id = #{vendOffice.id}
AND a.inv_status = #{invStatus}
AND a.biz_status = #{bizStatus}
AND a.plateform_id = #{plateformInfo.id}
AND process.type = #{commonProcess.type}
AND NOT EXISTS ( SELECT 1 FROM sys_test_account ta WHERE a.create_id = ta.object_id AND ta.object_name = 'sys_user' )
AND a.total_detail >= #{startPrice}
AND #{endPrice} >= a.total_detail
AND ppo.pay_time >= #{startPayTime}
AND ppo.pay_time <= #{endPayTime}
GROUP BY a.id
ORDER BY ${page.orderBy}
ORDER BY updateDate DESC, id DESC

SELECT 

FROM biz_po_header a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_po_header SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE biz_po_header SET
	current_payment_id = #{paymentId}
WHERE id = #{id}

UPDATE biz_po_header SET
	biz_status = #{status}
WHERE id = #{id}

UPDATE biz_po_header SET
process_id  = #{processId}
WHERE id = #{headerId}

UPDATE biz_po_header SET
pay_total  = (pay_total + #{payTotal})
WHERE id = #{id}
