mapper.select:
SELECT 

FROM biz_po_header a

WHERE a.id = #{id}

SELECT * FROM ( SELECT
FROM biz_po_header a
LEFT JOIN biz_po_order_req por ON por.po_id = a.id AND por.so_type = 1 LEFT JOIN biz_order_header oh ON oh.id = por.so_id
a.status = #{DEL_FLAG_NORMAL} and a.is_prew = 0
AND oh.order_num LIKE '%'||#{num}||'%' '%'+#{num}+'%' concat('%',#{num},'%')
AND a.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
AND a.vend_id = #{vendOffice.id}
AND a.inv_status = #{invStatus}
AND a.biz_status = #{bizStatus}
AND a.plateform_id = #{plateformInfo.id}
GROUP BY a.id ) n UNION SELECT * FROM ( SELECT
FROM biz_po_header b
b.status = #{DEL_FLAG_NORMAL} and b.is_prew = 0
AND rh.req_no LIKE '%'||#{num}||'%' '%'+#{num}+'%' concat('%',#{num},'%')
AND b.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
AND b.vend_id = #{vendOffice.id}
AND b.inv_status = #{invStatus}
AND b.biz_status = #{bizStatus}
AND b.plateform_id = #{plateformInfo.id}
GROUP BY b.id ) m
ORDER BY ${page.orderBy}
ORDER BY updateDate DESC

SELECT 

FROM biz_po_header a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_po_header SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE biz_po_header SET
	current_payment_id = #{paymentId}
WHERE id = #{id}

UPDATE biz_po_header SET
	biz_status = #{status}
WHERE id = #{id}

UPDATE biz_po_header SET
process_id  = #{processId}
WHERE id = #{headerId}

UPDATE biz_po_header SET
pay_total  = (pay_total + #{payTotal})
WHERE id = #{id}
