mapper.select:
SELECT 

FROM biz_scheduling_plan a

WHERE a.id = #{id}

SELECT

FROM biz_scheduling_plan a

WHERE a.object_id = #{objectId}

SELECT
,bcp.id AS "completePalnList.id" ,bcp.scheduling_id AS "completePalnList.schedulingId" ,bcp.complete_num AS "completePalnList.completeNum" ,bcp.plan_date AS "completePalnList.planDate" FROM biz_scheduling_plan a LEFT JOIN biz_complete_paln bcp ON bcp.scheduling_id = a.id
a.status = #{DEL_FLAG_NORMAL}
AND a.object_name = "biz_po_detail" AND a.object_id = #{bizPoDetail.id}
ORDER BY ${page.orderBy}
GROUP BY a.id

SELECT
,bcp.id AS "completePalnList.id" ,bcp.scheduling_id AS "completePalnList.schedulingId" ,bcp.complete_num AS "completePalnList.completeNum" ,bcp.plan_date AS "completePalnList.planDate" FROM biz_scheduling_plan a LEFT JOIN biz_complete_paln bcp ON bcp.scheduling_id = a.id
a.status = #{DEL_FLAG_NORMAL}
AND a.object_name = "biz_po_detail" AND a.object_id = #{bizPoDetail.id}
ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_scheduling_plan SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
