mapper.select:
SELECT 

FROM biz_prod_prop_value a

WHERE a.id = #{id}

SELECT
FROM biz_prod_prop_value a
a.status = #{DEL_FLAG_NORMAL}
AND a.prop_name LIKE '%'||#{propName}||'%' '%'+#{propName}+'%' concat('%',#{propName},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM biz_prod_prop_value a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_prod_prop_value SET prod_prop_id = #{prodPropertyInfo.id},
prop_id = #{propertyInfo.id}, prop_value_id = #{sysPropValue.id},
prop_name = #{propName}, prop_value = #{propValue}, source = #{source}, update_id = #{updateBy.id}, update_time = #{updateDate}, u_version = #{uVersion} WHERE id = #{id}

UPDATE biz_prod_prop_value SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
