mapper.select:
SELECT 

FROM biz_prod_prop_value a

WHERE a.id = #{id}

SELECT 

FROM biz_prod_prop_value a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.prop_name LIKE 
			'%'||#{propName}||'%'
			'%'+#{propName}+'%'
			concat('%',#{propName},'%')


		AND a.source =#{source}


		AND a.prod_prop_id =#{prodPropertyInfo.id}


		and ppi.prod_id = #{prodPropertyInfo.productInfo.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_time DESC

SELECT 

FROM biz_prod_prop_value a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_prod_prop_value SET prod_prop_id = #{prodPropertyInfo.id},
prop_id = #{propertyInfo.id}, prop_value_id = #{sysPropValue.id},
prop_name = #{propName}, prop_value = #{propValue}, code = #{code}, source = #{source}, update_id = #{updateBy.id}, update_time = #{updateDate}, u_version = #{uVersion} WHERE id = #{id}

UPDATE biz_prod_prop_value SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
