mapper.select:
SELECT 

FROM biz_prod_property_info a

WHERE a.id = #{id}

SELECT
FROM biz_prod_property_info a
a.status = #{DEL_FLAG_NORMAL}
AND a.prop_name LIKE '%'||#{propName}||'%' '%'+#{propName}+'%' concat('%',#{propName},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM biz_prod_property_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_prod_property_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
