mapper.select:
SELECT
FROM biz_product_info a
a.status = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.prod_code LIKE '%'||#{prodCode}||'%' '%'+#{prodCode}+'%' concat('%',#{prodCode},'%')
AND a.brand_name LIKE '%'||#{brandName}||'%' '%'+#{brandName}+'%' concat('%',#{brandName},'%')
AND a.vendor_id = #{office.id}
ORDER BY ${page.orderBy}
ORDER BY a.update_time DESC

SELECT 

FROM biz_product_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_product_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.insert:
INSERT INTO biz_prod_cate(prod_id, cat_id,create_id,create_time,update_id,update_time)

	SELECT #{id}, #{cate.id},#{createBy.id},#{createDate},#{updateBy.id},#{updateDate}

		FROM dual
