mapper.select:
SELECT 

FROM biz_variety_attr a

WHERE a.id = #{id}

SELECT
FROM biz_variety_attr a
a.status = #{DEL_FLAG_NORMAL}
AND a.variety_id = #{varietyInfo.id}
AND bvi.name LIKE '%'||#{varietyInfo.name}||'%' '%'+#{varietyInfo.name}+'%' concat('%',#{varietyInfo.name},'%')
AND a.attribute_id = #{attributeInfo.id}
AND sai.name LIKE '%'||#{attributeInfo.name}||'%' '%'+#{attributeInfo.name}+'%' concat('%',#{attributeInfo.name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM biz_variety_attr a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_variety_attr SET
status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
