mapper.select:
SELECT
FROM biz_request_detail a
a.status = #{DEL_FLAG_NORMAL}
AND a.sku_id = #{skuInfo.id}
AND si.item_no LIKE '%'||#{skuInfo.itemNo}||'%' '%'+#{skuInfo.itemNo}+'%' concat('%',#{skuInfo.itemNo},'%')
AND bpi.vendor_name LIKE '%'||#{skuInfo.vendorName}||'%' '%'+#{skuInfo.vendorName}+'%' concat('%',#{skuInfo.vendorName},'%')
AND si.part_no LIKE '%'||#{skuInfo.partNo}||'%' '%'+#{skuInfo.partNo}+'%' concat('%',#{skuInfo.partNo},'%')
AND a.header_id = #{requestHeader.id}
AND a.line_no = #{lineNo}
ORDER BY ${page.orderBy}

SELECT 

FROM biz_request_detail a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

SELECT GROUP_CONCAT( rd.id ) AS "reqDetailIds", sum( rd.req_qty ) AS "totalReqQty", sum( rd.send_qty ) AS "totalSendQty", sum( rd.recv_qty ) AS "totalRecvQty", pi.vendor_id AS "vendorId", pi.vendor_name AS "vendorName" FROM (SELECT a.id , a.sku_id,a.req_qty,a.send_qty,a.recv_qty,a.header_id,a.line_no FROM biz_request_detail a WHERE a.STATUS = 1 AND NOT EXISTS ( SELECT por.so_line_no, por.so_id FROM biz_po_order_req por WHERE por.status = 1 AND por.so_line_no = a.line_no AND por.so_id = a.header_id AND por.so_type = 2 ) ) rd LEFT JOIN biz_request_header rh ON rd.header_id = rh.id JOIN biz_sku_info si ON si.id = rd.sku_id JOIN biz_product_info pi ON pi.id = si.prod_id WHERE rh.STATUS = 1
AND rh.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}
AND rh.biz_status != #{bizStatusNot}
GROUP BY pi.vendor_id

mapper.update:
UPDATE biz_request_detail SET
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id AS "id", a.header_id AS "requestHeader.id", reheader.req_no AS "requestHeader.reqNo", a.line_no AS "lineNo", a.sku_id AS "skuInfo.id", a.unit_price AS "unitPrice", si.item_no AS "skuInfo.itemNo", bpi.vendor_name AS "skuInfo.vendorName", si.buy_price AS "skuInfo.buyPrice", a.req_qty AS "reqQty", a.recv_qty AS "recvQty", a.send_qty AS "sendQty", a.remark AS "remark", a.status AS "delFlag", a.create_id AS "createBy.id", a.create_time AS "createDate", a.u_version AS "uVersion", a.update_id AS "updateBy.id", a.update_time AS "updateDate",
ph.id AS "bizPoHeader.id", ph.order_num AS "bizPoHeader.orderNum",
bii.name as "invName", bis.stock_qty as "skuInvQty" FROM biz_request_detail a LEFT JOIN biz_request_header reheader ON a.header_id = reheader.id LEFT JOIN biz_sku_info si on si.id= a.sku_id LEFT JOIN biz_product_info bpi ON bpi.id = si.prod_id
LEFT JOIN biz_po_order_req bpor ON bpor.so_id = reheader.id AND a.line_no = bpor.so_line_no and bpor.so_type=2 AND bpor.status = 1 LEFT JOIN biz_po_header ph ON ph.id = bpor.po_id and ph.status=1 AND ph.is_prew = 0
LEFT JOIN biz_inventory_sku bis on bis.sku_id=a.sku_id and bis.status=1 LEFT JOIN biz_inventory_info bii on bii.id=bis.inv_id and bii.status=1 and bii.cust_id=reheader.from_id
a.status = #{DEL_FLAG_NORMAL}
AND a.sku_id = #{skuInfo.id}
AND si.item_no LIKE '%'||#{skuInfo.itemNo}||'%' '%'+#{skuInfo.itemNo}+'%' concat('%',#{skuInfo.itemNo},'%')
AND bpi.vendor_name LIKE '%'||#{skuInfo.vendorName}||'%' '%'+#{skuInfo.vendorName}+'%' concat('%',#{skuInfo.vendorName},'%')
AND si.part_no LIKE '%'||#{skuInfo.partNo}||'%' '%'+#{skuInfo.partNo}+'%' concat('%',#{skuInfo.partNo},'%')
AND a.header_id = #{requestHeader.id}
AND a.line_no = #{lineNo}
ORDER BY ${page.orderBy}
GROUP BY a.id

mapper.update:
UPDATE biz_request_detail SET
	out_qty = #{outQty}
WHERE id = #{id}
