mapper.select:
SELECT 

FROM biz_request_detail a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.sku_id = #{skuInfo.id}


		AND a.header_id = #{requestHeader.id}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_request_detail a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

SELECT GROUP_CONCAT( rd.id ) AS "reqDetailIds", sum( rd.req_qty ) AS "totalReqQty", sum( rd.send_qty ) AS "totalSendQty", sum( rd.recv_qty ) AS "totalRecvQty", pi.vendor_id AS "vendorId", pi.vendor_name AS "vendorName" FROM biz_request_detail rd LEFT JOIN biz_request_header rh ON rd.header_id = rh.id JOIN biz_sku_info si ON si.id = rd.sku_id JOIN biz_product_info pi ON pi.id = si.prod_id WHERE rh.STATUS = 1
AND rh.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}
GROUP BY pi.vendor_id

mapper.update:
UPDATE biz_request_detail SET
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
