mapper.update:
UPDATE biz_request_expand SET 
	status = #{DEL_FLAG_DELETE},
	u_version = #{uVersion},
	update_id = #{updateBy.id},
	update_time = #{updateDate}
WHERE id = #{id}

UPDATE biz_request_expand SET
balance_total  = (balance_total + #{payTotal})
WHERE id = #{id}

UPDATE biz_request_expand SET
current_payment_id = #{paymentId}
WHERE id = #{id}

