mapper.select:
		SELECT
			,
			pi.vendor_name AS "name",
			bvi.id as "varietyInfo.id",
			bvi.name as "varietyInfo.name"
		FROM biz_request_header a

		LEFT JOIN biz_sku_info bsi ON brd.sku_id = bsi.id AND bsi.status=1
		LEFT JOIN biz_product_info pi ON bsi.prod_id = pi.id AND pi.status=1


			LEFT JOIN sys_office sv ON sv.id = pi.vendor_id


		LEFT JOIN biz_variety_info bvi on bvi.id=pi.vari_id and bvi.status=1

			1=1

				and a.status = #{DEL_FLAG_NORMAL}


				AND a.header_type = #{headerType}


				and bvi.id = #{varietyInfo.id}


				AND process.type = #{process}


				AND a.req_no
				LIKE
				'%'||#{reqNo}||'%'
				'%'+#{reqNo}+'%'
				concat('%',#{reqNo},'%')


				AND a.req_type = #{reqType}


				AND a.from_id = #{fromOffice.id}


				AND a.from_type = #{fromType}


				AND  a.recv_eta BETWEEN ${startDate} AND ${endDate}


				AND a.biz_status = #{bizStatus}


				AND a.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}


				AND a.create_id = #{createBy.id}


				AND  pi.vendor_name = #{name}



				AND NOT EXISTS(
				select 1
				from sys_test_account ta
				where a.from_id = ta.object_id
				and ta.object_name = 'sys_office'
				)



				${sqlMap.request}


                AND  sv.id = ${vendorId}


                AND  bph.id = ${bizPoHeader.id}

			GROUP BY a.id



				ORDER BY ${page.orderBy}







				order by createDate desc


				order by updateDate desc

SELECT , pi.vendor_id AS "onlyVendor" FROM (select @rownum:=0) as rownum,biz_request_header a
INNER JOIN biz_sku_info bsi on brd.sku_id = bsi.id INNER JOIN biz_product_info pi ON bsi.prod_id = pi.id
1=1 AND EXISTS( SELECT 1 FROM (SELECT DISTINCT a.id, pi.vendor_id FROM biz_request_header a,biz_request_detail bod LEFT JOIN biz_sku_info bsi on bod.sku_id = bsi.id LEFT JOIN biz_product_info pi ON bsi.prod_id = pi.id WHERE bod.header_id = a.id AND bod.status = 1
and a.status = #{DEL_FLAG_NORMAL}
AND a.from_type = #{fromType}
AND a.req_no LIKE '%'||#{reqNo}||'%' '%'+#{reqNo}+'%' concat('%',#{reqNo},'%')
AND a.req_type = #{reqType}
AND a.from_id = #{fromOffice.id}
AND a.recv_eta BETWEEN ${startDate} AND ${endDate}
AND a.biz_status = #{bizStatus}
AND a.biz_status BETWEEN #{bizStatusStart} and #{bizStatusEnd}
AND a.create_id = #{createBy.id}
AND a.STATUS =1 ) t WHERE a.id = t.id GROUP BY id HAVING count(1) = 1 )
AND NOT EXISTS( select 1 from sys_test_account ta where a.from_id = ta.object_id and ta.object_name = 'sys_office' )
${sqlMap.request}
GROUP BY a.id
ORDER BY ${page.orderBy}
order by updateDate desc

SELECT 

FROM biz_request_header a


	a.status = #{DEL_FLAG_NORMAL}

GROUP BY a.id


		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_request_header SET 
	status = #{delFlag}
WHERE id = #{id}

UPDATE biz_request_header SET
process_id  = #{processId}
WHERE id = #{headerId}

UPDATE biz_request_header SET
balance_total  = (balance_total + #{payTotal})
WHERE id = #{id}

UPDATE biz_request_header SET
current_payment_id = #{paymentId}
WHERE id = #{id}

UPDATE biz_request_header SET
biz_status = #{status},
update_id = #{updateBy.id},
update_time = #{updateDate}
WHERE id = #{id}

mapper.select:
SELECT a.id AS "id", a.header_type AS "headerType", inv.name AS "invInfo.name", inv.id AS "invInfo.id", cent.name AS "fromOffice.name", a.from_type AS "fromType", a.req_no AS "reqNo", prod.vendor_name AS "vendName", prod.vari_id AS "varietyInfo.id", bvi.name AS "varietyInfo.name", process.id AS "invCommonProcess.id", process.object_id AS "invCommonProcess.objectId", process.object_name AS "invCommonProcess.objectName", process.prev_id AS "invCommonProcess.prevId", process.biz_status AS "invCommonProcess.bizStatus", process.processor AS "invCommonProcess.processor", process.description AS "invCommonProcess.description", process.type AS "invCommonProcess.type", process.current AS "invCommonProcess.current" FROM biz_request_header a LEFT JOIN common_process process ON process.object_id = a.id AND process.object_name = 'biz_inv_sku_request' AND process.current = 1 LEFT JOIN sys_office cent ON cent.id = a.from_id LEFT JOIN biz_request_detail rd ON rd.header_id = a.id LEFT JOIN biz_sku_info sku ON sku.id = rd.sku_id LEFT JOIN biz_product_info prod ON prod.id = sku.prod_id LEFT JOIN biz_variety_info bvi ON bvi.id = prod.vari_id LEFT JOIN biz_collect_goods_record bcgr ON bcgr.request_id = a.id LEFT JOIN biz_inventory_info inv ON inv.id = bcgr.inv_id WHERE inv.id IS NOT NULL
AND prod.vendor_name LIKE '%'||#{vendName}||'%' '%'+#{vendName}+'%' concat('%',#{vendName},'%')
AND prod.vari_id = #{varietyInfo.id}
AND process.type = #{invCommonProcess.type}
AND a.from_type = #{fromType}
AND NOT EXISTS( select 1 from sys_test_account ta where a.from_id = ta.object_id and ta.object_name = 'sys_office' )
GROUP BY a.id
ORDER BY ${page.orderBy}
