mapper.select:
SELECT 

FROM biz_op_shelf_info a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		and a.type = #{type}


		${sqlMap.shelfInfo}


GROUP BY a.id


		ORDER BY ${page.orderBy}




ORDER BY a.id asc

SELECT 

FROM biz_op_shelf_info a


	a.status = #{DEL_FLAG_NORMAL}

GROUP BY a.id


		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_op_shelf_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
