mapper.select:
SELECT 

FROM biz_op_shelf_sku a

WHERE a.id = #{id}

		SELECT 

		FROM biz_op_shelf_sku a


			a.status = #{DEL_FLAG_NORMAL}


				AND bsi.name LIKE
				'%'||#{skuInfo.name}||'%'
				'%'+#{skuInfo.name}+'%'
				concat('%',#{skuInfo.name},'%')


				AND so.name = #{centerOffice.name}



				AND a.shelf_time BETWEEN #{shelfStartTime} and #{shelfEndTime}



                AND a.shelf_time >= #{shelfStartTime}



                AND a.shelf_time <= #{shelfEndTime}



				AND a.unShelf_time BETWEEN #{unShelfStartTime} and #{unShelfEndTime}



				AND a.unShelf_time >= #{unShelfStartTime}



				AND a.unShelf_time <= #{unShelfEndTime}



				AND a.shelf_id = #{opShelfInfo.id}






				ORDER BY ${page.orderBy}


				ORDER BY a.update_time DESC

SELECT 

FROM biz_op_shelf_sku a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_op_shelf_sku SET shelf_id = #{opShelfInfo.id}, sku_id = #{skuInfo.id},
center_id = #{centerOffice.id},
shelf_user = #{shelfUser.id}, shelf_qty = #{shelfQty}, org_price = #{orgPrice}, sale_price = #{salePrice}, min_qty = #{minQty}, max_qty = #{maxQty}, shelf_time = #{shelfTime}, unshelf_user = #{unshelfUser.name}, unshelf_time = #{unshelfTime}, priority = #{priority}, u_version = #{uVersion}, update_id = #{updateBy.id}, update_time = #{updateDate} WHERE id = #{id}

UPDATE biz_op_shelf_sku SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
