mapper.select:
SELECT 

FROM biz_op_shelf_sku a

WHERE a.id = #{id}

		SELECT 

		FROM biz_op_shelf_sku a


			a.status = #{DEL_FLAG_NORMAL}


					AND a.sku_id = #{skuInfo.id}


				AND bsi.name LIKE
				'%'||#{skuInfo.name}||'%'
				'%'+#{skuInfo.name}+'%'
				concat('%',#{skuInfo.name},'%')


				AND bsi.item_no LIKE
				'%'||#{skuInfo.itemNo}||'%'
				'%'+#{skuInfo.itemNo}+'%'
				concat('%',#{skuInfo.itemNo},'%')


				AND bpi.vendor_name LIKE
				'%'||#{productInfo.vendorName}||'%'
				'%'+#{productInfo.vendorName}+'%'
				concat('%',#{productInfo.vendorName},'%')



            AND bsi.part_no LIKE
            '%'||#{skuInfo.partNo}||'%'
            '%'+#{skuInfo.partNo}+'%'
            concat('%',#{skuInfo.partNo},'%')


            AND so.name = #{centerOffice.name}



				AND a.shelf_time BETWEEN #{shelfStartTime} and #{shelfEndTime}



                AND a.shelf_time >= #{shelfStartTime}



                AND a.shelf_time <= #{shelfEndTime}



				AND a.unShelf_time BETWEEN #{unShelfStartTime} and #{unShelfEndTime}



				AND a.unShelf_time >= #{unShelfStartTime}



				AND a.unShelf_time <= #{unShelfEndTime}



				AND a.shelf_id = #{opShelfInfo.id}


				AND shelf.name LIKE
				'%'||#{opShelfInfo.name}||'%'
				'%'+#{opShelfInfo.name}+'%'
				concat('%',#{opShelfInfo.name},'%')



				${sqlMap.shelfSku}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_time DESC

SELECT 

FROM biz_op_shelf_sku a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_op_shelf_sku SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE biz_op_shelf_sku SET
unshelf_time = #{unshelfTime}
WHERE id = #{id}

UPDATE biz_op_shelf_sku SET
shelf_user = #{shelfUser.id},
shelf_time = #{shelfTime},
unshelf_time = #{unshelfTime}
WHERE id = #{id}
