mapper.select:
SELECT 

FROM biz_op_shelf_sku a

WHERE a.id = #{id}

SELECT
FROM biz_op_shelf_sku a
1=1
and a.status = #{DEL_FLAG_NORMAL}
AND a.sku_id = #{skuInfo.id}
AND bsi.name LIKE '%'||#{skuInfo.name}||'%' '%'+#{skuInfo.name}+'%' concat('%',#{skuInfo.name},'%')
AND bsi.item_no LIKE '%'||#{skuInfo.itemNo}||'%' '%'+#{skuInfo.itemNo}+'%' concat('%',#{skuInfo.itemNo},'%')
AND bpi.vendor_name LIKE '%'||#{productInfo.vendorName}||'%' '%'+#{productInfo.vendorName}+'%' concat('%',#{productInfo.vendorName},'%')
AND bpi.brand_name LIKE '%'||#{productInfo.brandName}||'%' '%'+#{productInfo.brandName}+'%' concat('%',#{productInfo.brandName},'%')
AND a.prod_id = #{productInfo.id}
AND bpi.name LIKE '%'||#{productInfo.name}||'%' '%'+#{productInfo.name}+'%' concat('%',#{productInfo.name},'%')
AND bsi.part_no LIKE '%'||#{skuInfo.partNo}||'%' '%'+#{skuInfo.partNo}+'%' concat('%',#{skuInfo.partNo},'%')
AND a.center_id = #{centerOffice.id}
AND so.name = #{centerOffice.name}
AND a.shelf_time BETWEEN #{shelfStartTime} and #{shelfEndTime}
AND a.shelf_time >= #{shelfStartTime}
AND a.shelf_time <= #{shelfEndTime}
AND a.unShelf_time BETWEEN #{unShelfStartTime} and #{unShelfEndTime}
AND a.unShelf_time >= #{unShelfStartTime}
AND a.unShelf_time <= #{unShelfEndTime}
AND a.shelf_id = #{opShelfInfo.id}
AND shelf.name LIKE '%'||#{opShelfInfo.name}||'%' '%'+#{opShelfInfo.name}+'%' concat('%',#{opShelfInfo.name},'%')
AND (a.unShelf_time IS NULL OR a.unShelf_time > CURRENT_TIME)
${sqlMap.shelfSku}
ORDER BY ${page.orderBy}
ORDER BY a.priority,a.id DESC

SELECT 

FROM biz_op_shelf_sku a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_op_shelf_sku SET 
	status = #{delFlag}
WHERE id = #{id}

UPDATE biz_op_shelf_sku SET
unshelf_time = #{unshelfTime}
WHERE id = #{id}

UPDATE biz_op_shelf_sku SET
shelf_user = #{shelfUser.id},
shelf_time = #{shelfTime},
unshelf_time = #{unshelfTime}
WHERE id = #{id}

update biz_op_shelf_sku oss,
	(SELECT boss.center_id,boss.shelf_id,boss.sku_id
		FROM biz_op_shelf_sku boss
		GROUP BY boss.center_id,boss.shelf_id,boss.sku_id
		order by boss.priority,boss.id DESC) t
set oss.priority=#{priority}
WHERE oss.center_id = t.center_id AND oss.shelf_id = t.shelf_id and oss.sku_id = t.sku_id

	and oss.center_id = #{centerOffice.id}

and oss.sku_id = #{skuInfo.id}
and oss.shelf_id  = #{opShelfInfo.id}

mapper.select:
SELECT

FROM biz_op_shelf_sku a

WHERE a.shelf_id = #{opShelfInfo.id}
AND a.sku_id = #{skuInfo.id}
AND a.id != #{id}
