mapper.select:
SELECT 

FROM biz_shelf_user a

WHERE a.id = #{id}

		SELECT 

		FROM biz_shelf_user a


            a.status = #{DEL_FLAG_NORMAL}

				AND a.shelf_id = #{shelfInfo.id}


				AND a.user_id = #{user.id}




				ORDER BY ${page.orderBy}

		SELECT 

		FROM biz_shelf_user a


            a.status = #{DEL_FLAG_NORMAL}



				ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_shelf_user SET 	
	shelf_id = #{shelfInfo.id},
	user_id = #{user.id}
WHERE id = #{id}

UPDATE biz_shelf_user SET
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
