SELECT 

FROM biz_shop_cart a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.sku_shelf_id = #{skuShelfinfo.id}


		AND a.office_id = #{office.id}


		AND a.user_id = #{user.id}


		AND a.create_id = #{createBy.id}


		AND a.update_id = #{updateBy.id}


		${sqlMap.shopCart}





		ORDER BY ${page.orderBy}

SELECT 

FROM biz_shop_cart a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

